package com.schachte.android.proactive_food_app.activities;

import android.content.DialogInterface;
import android.os.Bundle;
import android.support.v7.app.AlertDialog;
import android.support.v7.app.AppCompatActivity;
import android.view.View;
import android.widget.AdapterView;
import android.widget.ArrayAdapter;
import android.widget.ListView;

import com.schachte.android.proactive_food_app.R;
import com.schachte.android.proactive_food_app.models.Ingredient;
import com.schachte.android.proactive_food_app.models.PantryData;

import java.util.ArrayList;
import java.util.List;

public class PantryActivity extends AppCompatActivity {

    private ListView pantryItemsListView;

    private List<String> ingredientNames = new ArrayList<>();

    private ArrayAdapter<String> adapter;

    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        setContentView(R.layout.activity_pantry);

        pantryItemsListView = (ListView)findViewById(R.id.pantryListView);

        // Some test data
        PantryData pd = new PantryData();
        pd.addUserIngredient(new Ingredient("tester", "data:image/gif;base64,"));
        this.adapter = new ArrayAdapter<>(this, android.R.layout.simple_list_item_1, ingredientNames);

        this.pantryItemsListView.setAdapter(this.adapter);

        for (Ingredient ingredient : pd.getUserIngredients()) {
            this.ingredientNames.add(ingredient.getName());
            if (ingredient.getBlob() != null) {
                String blob = ingredient.getBlob();
            }
            this.adapter.notifyDataSetChanged();
        }

        this.pantryItemsListView.setOnItemClickListener(new AdapterView.OnItemClickListener() {
            @Override
            public void onItemClick(AdapterView<?> parent, View view, int position, long id) {
                AlertDialog.Builder ab = new AlertDialog.Builder(PantryActivity.this);
                ab.setTitle("Delete this item?");
                ab.setMessage("Are you sure you want to delete this item?");

                ab.setNegativeButton("Cancel", null);

                final int removePosition = position;

                ab.setPositiveButton("OK", new AlertDialog.OnClickListener() {

                    @Override
                    public void onClick(DialogInterface dialog, int which) {
                        ingredientNames.remove(removePosition);
                        adapter.notifyDataSetChanged();
                    }
                });
                ab.show();
            }
        });
    }
}
